import UIKit
import RxSwift
import RxCocoa

final class ShowDetailsViewController: UIViewController {

    // MARK: - Public properties -

    var presenter: ShowDetailsPresenterInterface!

    // MARK: - Private properties -

    @IBOutlet var tableView: UITableView!
    private let disposeBag = DisposeBag()
    private lazy var tableDataSource: TableDataSourceDelegate = {
        return TableDataSourceDelegate(tableView: tableView)
    }()
    // MARK: - Lifecycle -

    override func viewDidLoad() {
        super.viewDidLoad()
        setupView()
    }

}

// MARK: - Extensions -

extension ShowDetailsViewController: ShowDetailsViewInterface {
}

private extension ShowDetailsViewController {

    func setupView() {
        let output = ShowDetails.ViewOutput()

        let input = presenter.configure(with: output)
        handle(title: input.events.title)
        handle(items: input.models.sections)
    }

    func handle(title: Signal<String>) {
        title
            .emit(to: self.rx.title)
            .disposed(by: disposeBag)
    }

    func handle(items: Driver<[TableSectionItem]>) {
        items
            .drive(tableDataSource.rx.sections)
            .disposed(by: disposeBag)
    }

}
